import React, { useState, useEffect } from 'react';
import axios from 'axios';
import yaml from 'js-yaml';
import { Card, Button, Container, FormControl } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Masonry from 'react-masonry-css';
import './App.css';

const App = () => {
  const [allProjects, setAllProjects] = useState([]);
  const [filteredProjects, setFilteredProjects] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [Tags, setTags] = useState({
    Stage: new Set(['Preprocessing', 'Dimensionality Reduction', 'Quantitative Evaluation', 'Visualization']),
    Problems: new Set(['inaccurate', 'suboptimal', 'incomplete', 'unstable', 'uninterpretable', 'unscalable', 'irreflective', 'uninformed']),
    Aim: new Set(['Enhance reliability', 'Enhance awareness', 'Enhance Approachability']),
    Solution: new Set(['Improvements on DR', 'Improvements of Evaluation', 'Visualization Stage Solutions', 'DR framework', 'Visual Analytics System', 'Literature Review', 'Human-Centered Experiment', 'Computational Experiment']),
    GroupName: new Set(['Creator', 'Seeker', 'Instructor', 'Explorer', 'Interpreter', 'Architect'])
  });

  const [selectedTags, setSelectedTags] = useState(() => {
    const initialTags = {};
    Object.keys(Tags).forEach(category => {
      initialTags[category] = new Set();
    });
    return initialTags;
  });

  const shuffleArray = (array) => {
    return array.sort(() => Math.random() - 0.5);
  };
  const [yearRange, setYearRange] = useState([1964, 2024]); // Initialize with the oldest and latest year



  useEffect(() => {
    const fetchUrl = process.env.NODE_ENV === 'production' 
      ? 'https://taehyun.me/drsurvey_demo/papers2.yml' 
      : '/papers2.yml'; 

    axios.get(fetchUrl)
      .then(response => {
        const projects = yaml.load(response.data);
        const shuffledProjects = shuffleArray(projects);
        setAllProjects(shuffledProjects);
        setFilteredProjects(shuffledProjects);
      })
      .catch(error => console.error("Error loading or parsing YAML data:", error));
  }, []);

  useEffect(() => {
    const filterProjects = () => {
      const filtered = allProjects.filter(project =>
        Object.keys(selectedTags).every(category =>
          selectedTags[category].size === 0 || (
            project.Tags &&
            Array.from(selectedTags[category]).every(tag =>
              category === 'GroupName' ? project.Tags.GroupName === tag : project.Tags[tag] === 3.0
            )
          )
        ) &&
        (project.Title.toLowerCase().includes(searchQuery.toLowerCase()) ||
         project.Authors.toLowerCase().includes(searchQuery.toLowerCase()) ||
         project.Venue.toLowerCase().includes(searchQuery.toLowerCase()) ||
         project.venueA.toLowerCase().includes(searchQuery.toLowerCase()) ||
         project.Year.toString().includes(searchQuery)) &&
        project.Year >= yearRange[0] && project.Year <= yearRange[1] // Filter by year range
      );
      setFilteredProjects(filtered);
    };

    filterProjects();
  }, [selectedTags, allProjects, searchQuery, yearRange]);

  const handleYearChange = (event, index) => {
    const newRange = [...yearRange];
    newRange[index] = parseInt(event.target.value);
    if (newRange[0] <= newRange[1]) { // Prevent overlap
      setYearRange(newRange);
    }
  };

  const toggleTag = (category, tag) => {
    if (category === 'GroupName') {
      setSelectedTags(prev => ({
        ...prev,
        [category]: prev[category].has(tag) ? new Set() : new Set([tag])
      }));
    } else {
      const newSet = new Set(selectedTags[category]);
      if (newSet.has(tag)) {
        newSet.delete(tag);
      } else {
        newSet.add(tag);
      }
      setSelectedTags(prev => ({ ...prev, [category]: newSet }));
    }
  };

  const getTagsForProject = (project) => {
    let projectTags = [];
    Object.keys(Tags).forEach(category => {
      Tags[category].forEach(tag => {
        if (project.Tags && project.Tags[tag] === 3) {
          projectTags.push({ category, tag });
        }
      });
    });
    return projectTags;
  };

  const breakpointColumnsObj = {
    default: 3,
    1400: 3,
    1000: 2,
    700: 1
  };

  const handleCardClick = (doi) => {
    const googleSearchUrl = `https://www.google.com/search?q=doi:${encodeURIComponent(doi)}`;
    window.open(googleSearchUrl, '_blank');
  };


  return (
    <Container fluid className="app-container" style={{ paddingLeft: 0 }}>
      <div style={{ display: 'flex', height: '100vh', position: 'relative' }}>

        {/* Filter Panel */}
        <div 
          style={{ 
            width: '23%', 
            position: 'fixed', 
            left: 0, 
            top: 0, 
            height: '100vh', 
            overflowY: 'auto', 
            marginRight: '0px',
            backgroundColor: '#f7f7f7', 
            padding: '3px'
          }}
        >
          <h3>Filter Projects</h3>
                    {/* Search Bar */}
                    <FormControl
            type="text"
            placeholder="Search by Title, Author, Venue, or Year"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            style={{
              marginBottom: '15px',
              fontSize: '0.9rem',
              padding: '7px 10px',
              borderRadius: '5px',
              border: '1px solid #ccc',
            }}
          />
          {/* Year Range Slider */}
     {/* Year Range Slider */}
     <div style={{ marginBottom: '20px' }}>
          <label>Filter by Year:</label>
          <div style={{ position: 'relative', height: '40px', marginTop: '15px' }}>
            <div style={{ position: 'absolute', top: '15px', left: '0', right: '0', height: '4px', backgroundColor: '#ddd', borderRadius: '2px' }}></div>
            <div
              style={{
                position: 'absolute',
                top: '15px',
                left: `${((yearRange[0] - 1964) / (2024 - 1964)) * 100}%`,
                right: `${100 - ((yearRange[1] - 1964) / (2024 - 1964)) * 100}%`,
                height: '4px',
                backgroundColor: '#007bff',
                zIndex: 1,
                borderRadius: '2px',
              }}
            ></div>
            <input
              type="range"
              min={1964}
              max={2024}
              value={yearRange[0]}
              onChange={(e) => handleYearChange(e, 0)}
              style={{
                position: 'absolute',
                width: '100%',
                zIndex: 3, // Ensure left slider is on top
                pointerEvents: 'auto',
                background: 'none',
                appearance: 'none',
                height: '0',
                marginTop: '15px',
              }}
            />
            <input
              type="range"
              min={1964}
              max={2024}
              value={yearRange[1]}
              onChange={(e) => handleYearChange(e, 1)}
              style={{
                position: 'absolute',
                width: '100%',
                zIndex: 2, // Ensure right slider is beneath left when necessary
                pointerEvents: 'auto',
                background: 'none',
                appearance: 'none',
                height: '0',
                marginTop: '15px',
              }}
            />
          </div>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '10px' }}>
            <span style={{ position: 'absolute', left: `${((yearRange[0] - 1964) / (2024 - 1964)) * 100}%`, transform: 'translateX(-50%)', fontSize: '0.8rem' }}>{yearRange[0]}</span>
            <span style={{ position: 'absolute', left: `${((yearRange[1] - 1964) / (2024 - 1964)) * 100}%`, transform: 'translateX(-50%)', fontSize: '0.8rem' }}>{yearRange[1]}</span>
          </div>
        </div>

          {/* Group Names Filter */}
          <div>
            <h5>Group Names</h5>
            {Array.from(Tags.GroupName).map(tag => {
              const className = `group-name-btn group-name-${tag.toLowerCase()}`;
              return (
                <div
                  key={tag}
                  className={className}
                  onClick={() => toggleTag('GroupName', tag)}
                  style={{
                    opacity: selectedTags.GroupName.has(tag) ? 1 : 0.7,
                    fontWeight: selectedTags.GroupName.has(tag) ? 'bold' : 'normal',
                  }}
                >
                  {tag}
                </div>
              );
            })}
          </div>

          {/* Existing Filters */}
          {Object.keys(Tags)
            .filter(key => key !== 'GroupName')
            .map(category => (
              <div key={category}>
                <h5>{category}</h5>

                {Array.from(Tags[category]).map(tag => (
                  <Button
                    key={tag}
                    onClick={() => toggleTag(category, tag)}
                    variant={selectedTags[category].has(tag) ? 'secondary' : 'outline-secondary'}
                    className="mb-2"
                    style={
                      selectedTags[category].has(tag)
                        ? {
                            margin: '2px',
                            backgroundColor: 'Grey',
                            color: 'white',
                            border: 'none',
                            padding: '4px 7px',
                            borderRadius: '5px',
                            cursor: 'pointer',
                            fontSize: '0.65rem',
                          }
                        : {
                            margin: '2px',
                            backgroundColor: '#f0f0f0',
                            color: 'black',
                            border: 'none',
                            padding: '4px 7px',
                            borderRadius: '5px',
                            cursor: 'pointer',
                            fontSize: '0.65rem',
                          }
                    }
                  >
                    {tag}
                  </Button>
                ))}
              </div>
            ))}

          <div className="Tags" style={{ display: 'flex', justifyContent: 'space-between' }}>
            <Button
              id="reset-filter"
              onClick={() =>
                setSelectedTags({
                  Stage: new Set(),
                  Problems: new Set(),
                  Aim: new Set(),
                  Solution: new Set(),
                  GroupName: new Set(),
                })
              }
              style={{
                backgroundColor: 'red',
                color: 'white',
                border: 'none',
                padding: '4px 7px',
                borderRadius: '5px',
                cursor: 'pointer',
                fontSize: '0.7rem',
              }}
            >
              Reset X
            </Button>

            <Button
              id="serendipity-filter"
              onClick={() => setFilteredProjects(shuffleArray([...filteredProjects]))}
              style={{
                backgroundColor: 'grey',
                color: 'white',
                border: 'none',
                padding: '4px 7px',
                borderRadius: '5px',
                cursor: 'pointer',
                fontSize: '0.7rem',
              }}
            >
              Serendipity
            </Button>
          </div>

        </div>

        {/* Main Content Area */}
        <div 
          style={{ 
            flex: 1, 
            marginLeft: '23%', 
            overflowY: 'auto', 
            padding: '10px' 
          }}
        >


          <Masonry
            breakpointCols={breakpointColumnsObj}
            className="my-masonry-grid"
            columnClassName="my-masonry-grid_column"
          >
            {filteredProjects.map((project, index) => (
              <div
              key={index}
              className="card-body-wrapper"
              style={{
                borderColor: `var(--${project.Tags.GroupName.toLowerCase()})`,
                borderWidth: '4px',
                borderStyle: 'solid',
                borderRadius: '5px',
                padding: '10px',
                marginBottom: '1rem',
              }}
              onClick={() => handleCardClick(project.doi)}
            >

                <Card style={{ width: '100%', border: 'none', boxShadow: 'none' }}>
                  <Card.Body>
                    <Card.Title className="card-title">{project.Title}</Card.Title>
                    <Card.Text className="card-author">{project.Authors}</Card.Text>

                    <div>
                      {getTagsForProject(project).map(({ category, tag }) => (
                        <Button
                          key={`${category}-${tag}`}
                          onClick={(e) => {
                            e.stopPropagation(); // Prevent the card's onClick from firing
                            toggleTag(category, tag);
                          }}
                          variant={selectedTags[category].has(tag) ? 'secondary' : 'outline-secondary'}
                          className="mb-2"
                          style={
                            selectedTags[category].has(tag)
                              ? {
                                  margin: '2px',
                                  backgroundColor: 'Grey',
                                  color: 'white',
                                  border: 'none',
                                  padding: '4px 7px',
                                  borderRadius: '5px',
                                  cursor: 'pointer',
                                  fontSize: '0.6rem',
                                }
                              : {
                                  margin: '2px',
                                  backgroundColor: '#f0f0f0',
                                  color: 'black',
                                  border: 'none',
                                  padding: '4px 7px',
                                  borderRadius: '5px',
                                  cursor: 'pointer',
                                  fontSize: '0.6rem',
                                }
                          }
                        >
                          {tag}
                        </Button>
                      ))}
                    </div>

                    <div
                      style={{
                        marginTop: '5px',
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                      }}
                    >
                      <div
                        className={`card-group-name-btn card-group-name-${project.Tags.GroupName.toLowerCase()}`}
                        onClick={(e) => {
                          e.stopPropagation(); // Prevent the card's onClick from firing
                          toggleTag('GroupName', project.Tags.GroupName);
                        }}
                        style={{
                          cursor: 'pointer',
                          opacity: selectedTags.GroupName.has(project.Tags.GroupName) ? 1 : 0.7,
                          fontWeight: selectedTags.GroupName.has(project.Tags.GroupName) ? 'bold' : 'normal',
                        }}
                      >
                        {project.Tags.GroupName}
                      </div>
                      <Card.Text className="card-venue">
                        {project.venueA} {project.Year}
                      </Card.Text>
                    </div>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </Masonry>
        </div>
      </div>
    </Container>
  );
};

export default App;
