import React, { useState, useEffect } from 'react';
import axios from 'axios';
import yaml from 'js-yaml';
import { Card, Button, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [projects, setProjects] = useState([]);
  const [tags, setTags] = useState({
    Stage: new Set(['Preprocessing', 'Dimensionality Reduction', 'Quantitative Evaluation', 'Visualization']),
    Tools: new Set(['Inaccurate', 'Suboptimal', 'Incomplete', 'Unstable', 'Uninterpretability', 'Unscalable', 'Irrefective', 'Uninformed']),
    Aim: new Set(['Enhance reliability', 'Enhance awareness', 'Enhance Approachability']),
    Solution: new Set(['Improvements on DR', 'Improvements of Evaluation', 'Visualization Stage Solutions', 'DR framework', 'Visual Analytics System', 'Literature Review', 'Human-Centered Experiment', 'Computational Experiment'])
  });
  const [selectedTags, setSelectedTags] = useState({
    Stage: new Set(),
    Tools: new Set(),
    Aim: new Set(),
    Solution: new Set()
  });

  useEffect(() => {
    axios.get('/papers.yml')
      .then(response => {
        const parsedProjects = yaml.load(response.data);
        setProjects(parsedProjects);
      })
      .catch(error => console.error("Error loading or parsing YAML data:", error));
  }, []);

  const toggleTag = (category, tag) => {
    const newSet = new Set(selectedTags[category]);
    if (newSet.has(tag)) {
      newSet.delete(tag);
    } else {
      newSet.add(tag);
    }
    setSelectedTags({ ...selectedTags, [category]: newSet });
  };

  const filteredProjects = projects.filter(project =>
    Object.keys(selectedTags).every(category =>
      selectedTags[category].size === 0 || project.tags && project.tags.some(tag => selectedTags[category].has(tag))
    )
  );

  return (
    <Container fluid="md" className="app-container">
      <div style={{ display: 'flex', marginRight: '20px' }}>
        <div style={{ flex: '1' }}>
          <h3>Filter Projects</h3>
          {Object.keys(tags).map(category => (
            <div key={category}>
              <h5>{category}</h5>
              {Array.from(tags[category]).map(tag => (
                <Button
                  key={tag}
                  onClick={() => toggleTag(category, tag)}
                  variant={selectedTags[category].has(tag) ? 'secondary' : 'outline-secondary'}
                  className="mb-2"
                >
                  {tag}
                </Button>
              ))}
            </div>
          ))}
          <Button variant="danger" onClick={() => setSelectedTags({ Stage: new Set(), Tools: new Set(), Aim: new Set(), Solution: new Set() })}>
            Reset Filters
          </Button>
        </div>
        <div style={{ flex: '3', columnCount: 4, columnGap: '1rem' }}>
          {filteredProjects.map((project, index) => (
            <div key={index} style={{ marginBottom: '1rem' }}>
              <Card>
                {project.image && (
                  <Card.Img variant="top" src={`/assets/images/project/${project.image}`} />
                )}
                <Card.Body>
                  <Card.Title>{project.Title}</Card.Title>
                  <Card.Text>
                    {project.Authors}<br />
                    {project.Venue} - {project.Year}
                  </Card.Text>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>
    </Container>
  );
};

export default App;
