import React, { useState, useEffect } from 'react';
import axios from 'axios';
import yaml from 'js-yaml';
import { Card, Button, Container, Row, Col, Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [projects, setProjects] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);

  useEffect(() => {
    axios.get('/papers.yml')
      .then(response => {
        const parsedProjects = yaml.load(response.data);
        setProjects(parsedProjects);
      })
      .catch(error => console.error("Error loading or parsing YAML data:", error));
  }, []);

  const handleOpenModal = (project) => {
    setCurrentProject(project);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <Container className="mt-3">
      <Row xs={1} sm={2} md={3} lg={4} className="g-3">
        {projects.map((project, index) => (
          <Col key={index}>
            <Card className="h-100" onClick={() => handleOpenModal(project)}>
              {project.image && (
                <Card.Img variant="top" src={`/assets/images/project/${project.image}`} />
              )}
              <Card.Body>
                <Card.Title>{project.title}</Card.Title>
                <Card.Text>{project.description}</Card.Text>
                <div>
                  {project.tags && Object.entries(project.tags).map(([tag, value]) => (
                    <Button key={tag} variant="outline-secondary" size="sm" className="m-1">{tag}</Button>
                  ))}
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Modal show={showModal} onHide={handleCloseModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>{currentProject?.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {currentProject && <>
            <h4>{currentProject.authors}</h4>
            <p>{currentProject.description}</p>
          </>}
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default App;
