import React, { useState, useEffect } from 'react';
import Masonry from 'react-masonry-css';
import axios from 'axios';
import yaml from 'js-yaml';

const App = () => {
  const [projects, setProjects] = useState([]);
  const [tags, setTags] = useState({ Stage: new Set(), Tools: new Set(), Aim: new Set(), Solution: new Set() });
  const [selectedTags, setSelectedTags] = useState({ Stage: new Set(), Tools: new Set(), Aim: new Set(), Solution: new Set() });

  useEffect(() => {
    axios.get('/papers.yml')
      .then(response => {
        const parsedProjects = yaml.load(response.data);
        setProjects(parsedProjects);

        // Set predefined tags
        setTags({
          Stage: new Set(['Preprocessing', 'Dimensionality Reduction', 'Quantitative Evaluation', 'Visualization']),
          Tools: new Set(['Inaccurate', 'Suboptimal', 'Incomplete', 'Unstable', 'Uninterpretability', 'Unscalable', 'Irrefective', 'Uninformed']),
          Aim: new Set(['Enhance reliability', 'Enhance awareness', 'Enhance Approachability']),
          Solution: new Set(['Improvements on DR', 'Improvements of Evaluation', 'Visualization Stage Solutions', 'DR framework', 'Visual Analytics System', 'Literature Review', 'Human-Centered Experiment', 'Computational Experiment'])
        });
      })
      .catch(error => console.error("Error loading or parsing YAML data:", error));
  }, []);

  const toggleTag = (category, tag) => {
    const newSet = new Set(selectedTags[category]);
    if (newSet.has(tag)) {
      newSet.delete(tag);
    } else {
      newSet.add(tag);
    }
    setSelectedTags({ ...selectedTags, [category]: newSet });
  };

  const filteredProjects = projects.filter(project =>
    Object.keys(selectedTags).every(category =>
      Array.from(selectedTags[category]).every(tag =>
        project.tags && project.tags.includes(tag)
      )
    )
  );

  // Button styling for both filter and reset buttons
  const buttonStyle = {
    background: 'grey',
    color: 'black',
    border: 'none',
    padding: '4px 7px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '0.5rem',
    margin: '5px',
    display: 'block'
  };

  return (
    <div className="app-container" style={{ display: 'flex' }}>
      <div className="filter-section" style={{ flex: '1', marginRight: '20px' }}>
        <h3>Filter Projects</h3>
        {Object.keys(tags).map(category => (
          <div key={category} className="category">
            <h5>{category}</h5>
            <div className="tags">
              {Array.from(tags[category]).map(tag => (
                <button
                  key={tag}
                  onClick={() => toggleTag(category, tag)}
                  style={{ ...buttonStyle, background: selectedTags[category].has(tag) ? 'darkgrey' : 'grey' }}
                >
                  {tag}
                </button>
              ))}
            </div>
          </div>
        ))}
        <div className="tags">
          <button
            id="reset-filter"
            onClick={() => setSelectedTags({ Stage: new Set(), Tools: new Set(), Aim: new Set(), Solution: new Set() })}
            style={{ ...buttonStyle, background: 'red', color: 'white' }}
          >
            Reset X
          </button>
        </div>
      </div>
      <div className="layout-section" style={{ flex: '3' }}>
        <Masonry
          breakpointCols={{
            default: 4,
            1100: 3,
            700: 2,
            500: 1
          }}
          className="my-masonry-grid"
          columnClassName="my-masonry-grid_column">
          {filteredProjects.map((project, index) => (
            <div key={index} className="project-tile">
              <h4>{project.Title}</h4>
              <p>{project.Authors}</p>
              <p>{project.Venue} - {project.Year}</p>
            </div>
          ))}
        </Masonry>
      </div>
    </div>
  );
};

export default App;
