import React, { useState, useEffect } from 'react';
import axios from 'axios';
import yaml from 'js-yaml';
import { Modal, Button, Card, Container, Row, Col } from 'react-bootstrap';

const App = () => {
  const [projects, setProjects] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);

  useEffect(() => {
    axios.get('/papers.yml')
      .then(response => {
        const parsedProjects = yaml.load(response.data);
        setProjects(parsedProjects);
      })
      .catch(error => console.error("Error loading or parsing YAML data:", error));
  }, []);

  const handleShowModal = (project) => {
    setCurrentProject(project);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <Container className="mt-3">
      <Row xs={1} md={2} lg={4} className="g-4">
        {projects.map((project, idx) => (
          <Col key={idx}>
            <Card onClick={() => handleShowModal(project)}>
              <Card.Img variant="top" src={`/assets/images/project/${project.Image}`} />
              <Card.Body>
                <Card.Title>{project.Title}</Card.Title>
                <Card.Subtitle>{project.Authors} - {project.Venue} - {project.Year}</Card.Subtitle>
                <div>
                  {Object.entries(project.Tags).map(([tag, value]) =>
                    value === 3.0 && <Button variant="outline-secondary" size="sm" className="m-1">{tag}</Button>
                  )}
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {currentProject && (
        <Modal show={showModal} onHide={handleCloseModal} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">{currentProject.Title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <h4>{currentProject.Authors}</h4>
            <p>{currentProject.Detail}</p>
            {/* Display links or other elements as needed */}
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={handleCloseModal}>Close</Button>
          </Modal.Footer>
        </Modal>
      )}
    </Container>
  );
};

export default App;
