import React, { useState, useEffect } from 'react';
import Masonry from 'react-masonry-component';
import yaml from 'js-yaml';

const App = () => {
  const [projects, setProjects] = useState([]);
  const [tags, setTags] = useState(new Set());
  const [selectedTags, setSelectedTags] = useState(new Set());

  // Load and parse the YAML data
  useEffect(() => {
    fetch('/src/papers.yml') // Adjusted path for papers.yml in the public directory
      .then(response => response.text())
      .then(data => {
        const parsedData = yaml.load(data);
        if (Array.isArray(parsedData)) {
            setProjects(parsedData);
        } else {
            console.error('Data parsed is not an array:', parsedData);
            setProjects([]); // Set to empty array if data is not as expected
        }
      })
      .catch(error => {
        console.error('Error loading the YAML data:', error);
        setProjects([]); // Ensure projects is always an array
      });
}, []);


  // Handle tag selection
  const toggleTag = tag => {
    const newSet = new Set(selectedTags);
    if (newSet.has(tag)) {
      newSet.delete(tag);
    } else {
      newSet.add(tag);
    }
    setSelectedTags(newSet);
  };

  // Filter projects based on selected tags
  console.log('Projects:', projects);

  const filteredProjects = projects.filter(project =>
    Array.from(selectedTags).every(tag => project.tags && project.tags.includes(tag))
  );

  return (
    <div className="app-container" style={{ display: 'flex' }}>
      <div className="filter-section" style={{ flex: '1', marginRight: '20px' }}>
        <h3>Filter Projects</h3>
        {Array.from(tags).map(tag => (
          <button
            key={tag}
            onClick={() => toggleTag(tag)}
            style={{ display: 'block', margin: '5px', background: selectedTags.has(tag) ? 'grey' : 'white' }}
          >
            {tag}
          </button>
        ))}
      </div>
      <div className="layout-section" style={{ flex: '3' }}>
        <Masonry>
          {filteredProjects.map((project, index) => (
            <div key={index} className="project-tile">
              <h4>{project.Title}</h4>
              <p>{project.Authors}</p>
              <p>{project.Venue} - {project.Year}</p>
            </div>
          ))}
        </Masonry>
      </div>
    </div>
  );
};

export default App;
