import React, { useState, useEffect } from 'react';
import axios from 'axios';
import yaml from 'js-yaml';
import { Card, Button, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Masonry from 'react-masonry-css';
import './App.css';  // Ensure this CSS file contains the above CSS

const App = () => {
  const [allProjects, setAllProjects] = useState([]); // Stores all projects
  const [filteredProjects, setFilteredProjects] = useState([]); // Stores filtered projects
  const [Tags, setTags] = useState({
    Stage: new Set(['Preprocessing', 'Dimensionality Reduction', 'Quantitative Evaluation', 'Visualization']),
    Problems: new Set(['inaccurate', 'suboptimal', 'incomplete', 'unstable', 'uninterpretable', 'unscalable', 'irreflective', 'uninformed']),
    Aim: new Set(['Enhance reliability', 'Enhance awareness', 'Enhance Approachability']),
    Solution: new Set(['Improvements on DR', 'Improvements of Evaluation', 'Visualization Stage Solutions', 'DR framework', 'Visual Analytics System', 'Literature Review', 'Human-Centered Experiment', 'Computational Experiment']),
    GroupName: new Set(['Creator', 'Seeker', 'Instructor', 'Explorer', 'Interpreter', 'Architect'])
  });

  const [selectedTags, setSelectedTags] = useState({
    Stage: new Set(),
    Problems: new Set(),
    Aim: new Set(),
    Solution: new Set()
  });

  useEffect(() => {
    axios.get('/papers2.yml')
      .then(response => {
        const projects = yaml.load(response.data);
        setAllProjects(projects);
        setFilteredProjects(projects); // Initialize filtered projects as all projects
        console.log("All projects loaded:", projects);
      })
      .catch(error => console.error("Error loading or parsing YAML data:", error));
  }, []);

  useEffect(() => {
    const filterProjects = () => {
      const filtered = allProjects.filter(project =>
        Object.keys(selectedTags).every(category =>
          selectedTags[category].size === 0 || (
            project.Tags &&
            Array.from(selectedTags[category]).every(tag => 
              project.Tags[tag] === 3.0
            )
          )
        )
      );
      setFilteredProjects(filtered);
    };

    filterProjects(); // Filter projects whenever selectedTags change
  }, [selectedTags, allProjects]);

  const toggleTag = (category, tag) => {
    const newSet = new Set(selectedTags[category]);
    if (newSet.has(tag)) {
      newSet.delete(tag);
    } else {
      newSet.add(tag);
    }
    setSelectedTags(prev => ({ ...prev, [category]: newSet }));
  };

  const getTagsForProject = (project) => {
    let projectTags = [];

    Object.keys(Tags).forEach(category => {
      console.log("projecttags", project.Title, project.Tags);
      Tags[category].forEach(tag => {
        // Check if the tag exists and equals 3
        if (project.Tags && project.Tags[tag] === 3) {
          projectTags.push({ category, tag });
        }
      });
    });

    return projectTags;
  };


  const breakpointColumnsObj = {
    default: 3,
    1400: 3,
    1000: 2,
    700: 1
  };

  return (
    <Container fluid="md" className="app-container">
      <div style={{ display: 'flex', height: '100vh', marginRight: '20px' }}>
      <div style={{ width: '20%', position: 'sticky', top: 0, height: '100vh', overflowY: 'auto' }}>
  <h3>Filter Projects</h3>

  {/* Group Names Filter */}
  <div>
    <h5>Group Names</h5>
    {Array.from(Tags.GroupName).map(tag => (
      <Button
        key={tag}
        onClick={() => toggleTag('GroupName', tag)}
        variant={selectedTags.GroupName.has(tag) ? 'secondary' : 'outline-secondary'}
        className="mb-2"
        style={selectedTags.GroupName.has(tag) ? { margin: "2px", backgroundColor: 'Grey', color: 'white', border: 'none', padding: '4px 7px', borderRadius: '5px', cursor: 'pointer', fontSize: '0.5rem' } : { margin: "2px", backgroundColor: '#f0f0f0', color: 'black', border: 'none', padding: '4px 7px', borderRadius: '5px', cursor: 'pointer', fontSize: '0.5rem' }}
      >
        {tag}
      </Button>
    ))}
  </div>

  {/* Existing Filters */}
  {Object.keys(Tags).filter(key => key !== 'GroupName').map(category => (
    <div key={category}>
      <h5>{category}</h5>
      {Array.from(Tags[category]).map(tag => (
        <Button
          key={tag}
          onClick={() => toggleTag(category, tag)}
          variant={selectedTags[category].has(tag) ? 'secondary' : 'outline-secondary'}
          className="mb-2"
          style={selectedTags[category].has(tag) ? { margin: "2px", backgroundColor: 'Grey', color: 'white', border: 'none', padding: '4px 7px', borderRadius: '5px', cursor: 'pointer', fontSize: '0.5rem' } : { margin: "2px", backgroundColor: '#f0f0f0', color: 'black', border: 'none', padding: '4px 7px', borderRadius: '5px', cursor: 'pointer', fontSize: '0.5rem' }}
        >
          {tag}
        </Button>
      ))}
    </div>
  ))}

  <div className="Tags">
    <Button
      id="reset-filter"
      onClick={() => setSelectedTags({ Stage: new Set(), Problems: new Set(), Aim: new Set(), Solution: new Set(), GroupName: new Set() })}
      style={{ backgroundColor: 'red', color: 'white', border: 'none', padding: '4px 7px', borderRadius: '5px', cursor: 'pointer', fontSize: '0.5rem' }}
    >
      Reset X
    </Button>
  </div>
</div>

        <div style={{ flex: '3', overflowY: "auto" }}>
          <Masonry
            breakpointCols={breakpointColumnsObj}
            className="my-masonry-grid"
            columnClassName="my-masonry-grid_column"
          >
            {filteredProjects.map((project, index) => (
              <div key={index} className="card-wrapper">
                <Card style={{ marginBottom: '1rem', width: '100%' }}>
                  {project.image && (
                    <Card.Img variant="top" src={`/assets/images/project/${project.image}`} />
                  )}
                  <Card.Body>
                  <Card.Title className="card-title">{project.Title}</Card.Title>

                    <Card.Text className="card-author">{project.Authors}</Card.Text>


                    <div>
                      {getTagsForProject(project).map(({ category, tag }) => (
                        <Button
                          key={`${category}-${tag}`}
                          onClick={() => toggleTag(category, tag)}
                          variant={selectedTags[category].has(tag) ? 'secondary' : 'outline-secondary'}
                          className="mb-2"
                          style={selectedTags[category].has(tag) ? {margin: "2px", backgroundColor: 'Grey', color: 'white', border: 'none', padding: '4px 7px', borderRadius: '5px', cursor: 'pointer', fontSize: '0.5rem' } : { margin: "2px", backgroundColor: '#f0f0f0', color: 'black', border: 'none', padding: '4px 7px', borderRadius: '5px', cursor: 'pointer', fontSize: '0.5rem' }}
                        >
                          {tag}
                        </Button>
                      ))}
                    </div>

                    <Card.Text className="card-venue">
                        {project.venueA} {project.Year}
                        </Card.Text>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </Masonry>
        </div>
      </div>
    </Container>
  );
};

export default App;
